<script>
import TransactionTable from '../../components/TransactionTable.vue';
import { mapGetters, mapActions } from "vuex"
export default {
  components: { TransactionTable },
    name: "HomePage",
    data() {
        return {
            binanceEnabled: false,
            kucoinEnabled: false,
        }
    },
    computed: {
        ...mapGetters('user', ['platformConfig', 'tradeConfig']),
    },
    mounted() {
        this.initPlatformConfig();
    },
    methods: {
        ...mapActions("user",["updateUserPlatformConfig"]),
        ...mapActions("alert",["errorAlert"]),
        onPlatformToggle(platformName) {
            console.log(platformName);
            let config = {}
            if(Object.keys(this.tradeConfig ? this.tradeConfig : {}).length > 0) {
                if(platformName === 'binance') {
                    if(Object.keys(this.platformConfig.binanceConfig ? this.platformConfig.binanceConfig : {}).length > 0) {
                        config.platform = 'Binance'
                        config.platformConfig = { enabled: !this.platformConfig.binanceConfig.enabled }
                        this.updateUserPlatformConfig(config)
                    } else {
                        this.binanceEnabled = false
                        this.kucoinEnabled = false
                        this.errorAlert("Please configure your Binance account first")
                    }
                } else if(platformName === 'kucoin') {
                    if(Object.keys(this.platformConfig.kucoinConfig).length > 0) {
                        config.platform = 'Kucoin'
                        config.platformConfig = { enabled: !this.platformConfig.kucoinConfig.enabled }
                        this.updateUserPlatformConfig(config)
                    } else {
                        this.binanceEnabled = false
                        this.kucoinEnabled = false
                        this.errorAlert("Please configure your Kucoin account first")
                    }
                }
            } else {
                this.binanceEnabled = false
                this.kucoinEnabled = false
                this.errorAlert("Please configure your trade settings first")
            }  
        },
        initPlatformConfig() {
            if(Object.keys(this.platformConfig ? this.platformConfig : {}).length > 0) {
                if(Object.keys(this.platformConfig.binanceConfig ? this.platformConfig.binanceConfig : {}).length > 0) {
                this.binanceEnabled = this.platformConfig.binanceConfig.enabled
                }
                if(Object.keys(this.platformConfig.kucoinConfig ? this.platformConfig.kucoinConfig : {}).length > 0) {
                    this.kucoinEnabled = this.platformConfig.kucoinConfig.enabled
                }
            }
        }
    }
};
</script>

